import React from 'react';
import { Header, List } from 'semantic-ui-react'

class Results extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ingredients: ['양파', '버섯', '김치'],
            response: []
        };
    }

    componentDidMount() {
        const ingredients = this.state.ingredients
        const ingredientsString = ingredients.join(',')
        const url = 'http://localhost:9000/?query='.concat(ingredientsString)

        fetch(url)
           .then((result) => result.json())
           .then(response => {
            this.setState({response: response.Recipes})
          })
          .catch(err => {
            console.log(err);
          }); 
    }

    // componentDidMount() {
    //     this.callApi()
    //     .then(res => this.setState({ response: res.Recipes }))
    //     .catch(err => console.log(err));
    // }

    // callApi = async () => {
    //     const ingredients = this.state.ingredients
    //     const ingredientsString = ingredients.join(',')
    //     const query = 'http://localhost:9000/?query='.concat(ingredientsString)

    //     const response = await fetch(query);
    //     const body = await response.json();
    //     if (response.status !== 200) throw Error(body.message);

    //     return body;
    //   };

    render() {
       //const ingredients = this.state.ingredients
       const recipes = this.state.response

      return (
          <div>
              <Header as='h3'> Recipes </Header>
              <List>
              {recipes.forEach(function(recipe) { recipe => (
                  <List.Item>
                      <List.Content>
                          <List.Header>{recipe.ingredients}</List.Header>
                      </List.Content>
                  </List.Item>
              )

              //console.log(recipe.ingredients)
                  })}
              </List>
          </div>
      )
    }
}


export default Results;
