import React from 'react';
import { Input, Header, List, Icon } from 'semantic-ui-react'


class Fridge extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: ''};

        ingredients =  {'apple': 'apple', 'pear':'pear'}

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        //this.state.ingredients.push({ key: this.state.value, value: this.state.value })
        alert('A name was submitted: ' + this.state.ingreidents.keys);
        //event.preventDefault();
    }

    render() {
      return (
        <div>
          <Header as='h1'> 냉장고 </Header>
          <form onSubmit={this.handleSubmit}>
          <Input 
            type="text"
            placeholder='재료추가' 
            value={this.state.value}
            onChange={this.handleChange}
          />
          </form>
          <List selection verticalAlign='middle'>
              {Object.keys(this.state.ingredients).map((ingredient) => (
                  <List.Item>
                  <Icon color='orange' name='minus circle'/>
                  <List.Content>
                    <List.Header>{ingredient}</List.Header>
                  </List.Content>
                </List.Item>
              ))}
          </List>
        </div>
      );
    }
  }

  export default Fridge;
