import React from 'react';
import { Input, Header, List, Icon } from 'semantic-ui-react'


class Fridge extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            value: '',
            ingredients: []
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.removeIngredient = this.removeIngredient.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        const newIngredients = [...this.state.ingredients, this.state.value];
        this.setState({ingredients: newIngredients})
        this.setState({value: '재료추가'})

        event.preventDefault();
    }

    removeIngredient(ingredient) {
        const ingredients = this.state.ingredients;

        this.setState({
            ingredients: ingredients.filter((i) => {
                return i !== ingredient
              }),
        });
    }

    render() {

        const ingredients = this.state.ingredients

        return (
        <div>
          <Header as='h1'> 냉장고 </Header>
          <form onSubmit={this.handleSubmit}>
          <Input 
            type="text"
            placeholder='재료추가'
            onChange={this.handleChange}
          />
          </form>
          <List selection verticalAlign='middle'>
              {ingredients.map((ingredient) => (
                <List.Item onClick={this.removeIngredient(ingredient)}>
                  <Icon color='orange' name='minus circle'/>
                  <List.Content>
                    <List.Header>{ingredient}</List.Header>
                  </List.Content>
                </List.Item>
              ))}
          </List>
        </div>
      );
    }
  }

  export default Fridge;
